/**
 * Get template metadata by vertical folder and index
 *
 * @param verticalFolder - The vertical folder name (e.g., "Verticals - Fashion & Apparel")
 * @param index - The template index (0-based)
 * @returns The template metadata or null if not found
 */
export function getTemplateMetadata(verticalFolder: string, index: number): TemplateMetadata | null {
  const templates = TEMPLATE_METADATA[verticalFolder]
  if (!templates || index < 0 || index >= templates.length) {
    return null
  }
  return templates[index]
}
